files.js:
// Interactive functionality
document.addEventListener('DOMContentLoaded', () => {
  console.log('Challenge loaded');


  const form = document.querySelector('form');
  if (form) {
    form.addEventListener('submit', (e) => {
      e.preventDefault();
      console.log('Form submitted');
      alert('Success!');
    });
  }



});
